<?phpnamespace App;use Illuminate\Database\Eloquent\Model;use DB;class Category extends Model{	protected $table = 'category';		protected $primaryKey = 'id';		public $timestamps = true;		protected $fillable = array('name', 'name_ar', 'active', 'display_order', 'group_id', 'color');		public function questions($survey_id)	{		$category_id = $this->id;		$data = SurveyField::where('category_id', $category_id)					->where('survey_id', $survey_id)					->where('active', 1)					->orderBy('display_order', 'ASC')					->get();		return $data;	}}?>