<?phpnamespace App\Helper;use Intervention\Image\ImageManager;use App\Models\ImageUploadModel;use Image;use DB;class Custom{    public function upload_default_gallery($file)    {        $target_dir       = public_path('assets/manage_store/default_gallery/');        $target_file      = $target_dir . basename($file['name']);        $imageFileType    = pathinfo($target_file, PATHINFO_EXTENSION);        $unique_file_name = rand(10000, 990000) . '_' . time() . '.' . $imageFileType;        $path             = $target_dir . $unique_file_name;        $img              = Image::make($file['tmp_name'])->save($path);        return $unique_file_name;    }    public function image_resize($orignal_image_path, $copy_to_image_path, $width, $height, $resize_type)    {        if ($resize_type == 1 && !empty($height) && !empty($width)) {            $img = Image::make($orignal_image_path)->crop($width, $height)->save($copy_to_image_path);        } else if ($resize_type == 2) {            $img = Image::make($orignal_image_path)->resize($width, null, function($constraint)            {                $constraint->aspectRatio();            })->save($copy_to_image_path);        } else if ($resize_type == 3) {            $img = Image::make($orignal_image_path)->resize(null, $height, function($constraint)            {                $constraint->aspectRatio();            })->save($copy_to_image_path);        }    }}?>