<?phpnamespace App\Http\Controllers\admin;use Illuminate\Http\Request;use App\Http\Controllers\Controller as BaseController;use App\Group;use App\GeneralSetting;use Auth;class Controller extends BaseController{    public $response = array();    public $data = array();    public $rtl;    public function __construct()    {        parent::__construct();        $this->data['user_info'] = Auth::user();        $this->data['group_info'] = Group::where('id', session()            ->get('group_id'))            ->first();        if ($this->data['user_info'] && $this->data['user_info']            ->hasRole('administrator')) $this->data['groups'] = Group::all();        $this->data['roles'] = roles();		$this->data['general_setting'] = GeneralSetting::first();    }}