<?phpnamespace App\Http\Controllers\admin\dashboard;use Illuminate\Http\Request;use App\Http\Controllers\admin\Controller;use DB, View, Validator, Route, Auth, File;use App\Survey;use App\SurveySubmission;//use App\Category;use App\Group;//use App\User;use App\Employee;//use Spatie\Permission\Models\Role;class Dashboard extends Controller {    public function __construct() {        parent::__construct();    }    public function index(Request $request) {        $this->data['surveys'] = Survey::where('group_id', $this->data['group_info']            ->id)            ->count();        $submissions = SurveySubmission::where('group_id', $this->data['group_info']            ->id);		if($this->data['group_info']->id == 1) $submissions->where('p_status', 1);        $this->data['submissions'] = $submissions->count();        //$this->data['categories'] = Category::where('group_id', $this->data['group_info']->id)->count();        //$this->data['roles'] = Role::where('group_id', $this->data['group_info']->id)->count();        //$this->data['users'] = User::where('group_id', $this->data['group_info']->id)->count();        $this->data['employees'] = Employee::where('group_id', $this->data['group_info']            ->id)            ->count();        return View::make('admin.dashboard.index')            ->with('data', $this->data);    }}