<?phpnamespace App\Http\Middleware;use Closure, Request;class ForceSSL{    public function handle($request, Closure $next)    {		$url = Request::url();        if (!$request->secure()) {            return redirect()->secure($request->getRequestUri());        } else if($value = str_contains(\Request::url(), 'www.')) {			return redirect(str_replace('www.', '', $url));		}        return $next($request);    }}