<?phpnamespace App\Mail;use Illuminate\Bus\Queueable;use Illuminate\Mail\Mailable;use Illuminate\Queue\SerializesModels;use Illuminate\Contracts\Queue\ShouldQueue;class Email extends Mailable{	use Queueable, SerializesModels;    /**     * Create a new message instance.     *     * @return void     */    public $data;	    public function __construct($data)    {        $this->data = $data;    }    /**     * Build the message.     *     * @return $this     */    public function build()    {        $data = $this->subject($this->data['subject'])            ->view($this->data['view'])->withData($this->data);		if(isset($this->data['attachment'])) 		{			foreach($this->data['attachment'] as $key => $attahment) {				if(file_exists($attahment)) $data = $data->attach($attahment);			}		}		return $data;    }}