<?phpnamespace App\Providers;use Config;use Illuminate\Support\Facades\DB;use Illuminate\Support\ServiceProvider;class ConfigServiceProvider extends ServiceProvider{    /**     * Bootstrap the application services.     *     * @return void     */    public function boot()    {        //            }    /**     * Register the application services.     *     * @return void     */    public function register()    {        if (\Schema::hasTable('general_setting'))        {            $record = DB::table('general_setting')->first();            if ($record) //checking if table is not empty                        {                Config::set('app.name', $record->title);            }        }    }}