<?phpnamespace App;use Illuminate\Database\Eloquent\Model;use DB;class SurveyField extends Model{	protected $table = 'survey_field';		protected $primaryKey = 'id';		public $timestamps = true;		protected $fillable = array('label', 'label_ar', 'type', 'survey_id', 'active', 'require', 'is_grid', 'category_id', 'display_order');		public function options()	{		$data = SurveyFieldValue::where('survey_field_id', $this->id)					->where('survey_id', $this->survey_id)					->get();		return $data;	}}?>