<?phpnamespace App;use Illuminate\Database\Eloquent\Model;use App\SurveyAnswer;use DB;class SurveySubmission extends Model{	protected $table = 'survey_submission';		protected $primaryKey = 'id';		public $timestamps = true;		protected $fillable = array('group_id', 'survey_id', 'b', 'a', 'l', 'r', 'image', 'employee_code', 'behavior', 'sub_behavior', 'amount', 'p_status','session_id');		public function answers($survey_field_id)	{		$data = SurveyAnswer::leftJoin('survey_field_value', function ($join) {					$join->on('survey_field_value.id', '=', 'survey_answer.survey_field_value_id');				})				->where('survey_answer.survey_submission_id', $this->id)				->where('survey_answer.survey_field_id', $survey_field_id)				->select('survey_answer.answer', 'survey_field_value.value')				->get();		return $data; 	}}?>