	var sessions = "{{route('admin-session-listing')}}";		var session_create = "{{route('admin-session-create')}}";		var session_update = "{{route('admin-session-update')}}";		var session_delete = "{{route('admin-session-delete')}}";		var session_survey = "{{route('admin-session-survey')}}";		var groups = "{{route('admin-groups')}}";		/*Permissions*/		var update_session = "{{(Auth::user()->hasPermissionTo('session update') || Auth::user()->hasRole('administrator') ? true : false)}}";		var delete_session = "{{(Auth::user()->hasPermissionTo('session delete') || Auth::user()->hasRole('administrator') ? true : false)}}";		$(document).on('click','#import_sessions',function(){			$('#upload_excel_modal').css('display','block');		});		$(document).on('submit','.file_upload_session_form',function(e){			$form = $(this);			var _action = $(this).attr('action');			$.ajax({				type: $form.attr('method'),				url: _action,				data: new FormData(this),				dataType: "json",				cache: false,				contentType: false,				processData: false,				success: function(response) {					//$("#czContainer").czMore();					//alert(response.status);					if (response.status === true) {						$('#import_sessions_modal').hide();						window.location.reload();					} else if (response.status === false) {						alert(response.message);					}				},				error: function(xhr, ajaxOptions, thrownError) {					alert(thrownError + "\r\n" + xhr.statusText + "\r\n" + xhr.responseText);				}			});		});		$(function() {			$('.msg_success').delay(5000).fadeOut('slow');		});	</script>		{{HTML::script('public/assets/bower_components/jtable/lib/jquery.jtable.min.js')}}	{{HTML::script('public/assets/admin/js/pages/session/index.js?v=2')}}@endpush@push('metainfo');	<title>Manage Sessions | {{ config('app.name') }}</title>@endpush