<!doctype html><html><head>    <meta charset="utf-8">     <meta name="viewport" content="width=device-width, initial-scale=1.0">    <title>Registration | {{ config('app.name') }}</title>    <style type="text/css">         body {            color: #777;            padding: 0;            margin: 0;        }        body, table, td, p, a, li, blockquote {            -webkit-text-size-adjust: none !important;            font-style: normal;            font-weight: 400;            font-family: 'Roboto', sans-serif;        }        @import 'https://fonts.googleapis.com/css?family=Roboto';        @import 'url(http://fonts.googleapis.com/earlyaccess/notosanskannada.css)';        button {            width: 90%;        }        p {            font-size: 13px !important;            color: #777 !important;            line-height: normal !important;            padding-bottom: 14px !important;            margin: 0px !important;        }        @media screen and (max-width:600px) {            /*styling for objects with screen size less than 600px; */            body, table, td, p, a, li, blockquote {                -webkit-text-size-adjust: none!important;            }            table {                /* All tables are 100% width */                width: 100%;            }            .footer {                /* Footer has 2 columns each of 48% width */                height: auto !important;                max-width: 48% !important;                width: 48% !important;            }table.responsiveImage {                /* Container for images in catalog */                height: auto !important;                max-width: 30% !important;                width: 30% !important;            }            table.responsiveContent {                /* Content that accompanies the content in the catalog */                height: auto !important;                max-width: 66% !important;                width: 66% !important;            }            .top {                /* Each Columnar table in the header */                height: auto !important;                max-width: 48% !important;                width: 48% !important;            }            .catalog {                margin-left: 0%!important;            }        }        @media screen and (max-width:480px) {            /*styling for objects with screen size less than 480px; */            body, table, td, p, a, li, blockquote {            }            table {                /* All tables are 100% width */                width: 100% !important;                border-style: none !important;            }            .footer {                /* Each footer column in this case should occupy 96% width  and 4% is allowed for email client padding*/                height: auto !important;                max-width: 96% !important;                width: 96% !important;            }            .table.responsiveImage {                /* Container for each image now specifying full width */                height: auto !important;                max-width: 96% !important;                width: 96% !important;            }            .table.responsiveContent {                /* Content in catalog  occupying full width of cell */                height: auto !important;                max-width: 96% !important;                width: 96% !important;            }            .top {                /* Header columns occupying full width */                height: auto !important;                max-width: 100% !important;                width: 100% !important;            }            .catalog {                margin-left: 0%!important;            }            button {                width: 90%!important;            }        }    </style></head><body><table width="100%" cellspacing="0" cellpadding="0">    <tbody>    <tr>        <td>            <table width="600"  align="center" cellpadding="0" cellspacing="0" style="                color: #444;                background-color: #f4f4f4;                background-repeat: no-repeat;                background-size: 100% 100%;                background-position: top center;            ">                <tbody>                    <tr>                        <td style="padding: 45px 35px;">                            <table style="display: block;width: 100%;">                                <tbody style="display: block;width: 100%;">                                    <tr style="display: block;width: 100%;">                                        <td style="                                            display: block;                                            width: 100%;                                            color: #292929;                                            font-size: 20px;                                            font-weight: bold;                                            padding-bottom: 25px;                                            text-transform: uppercase;                                        ">                                            Dear {{$data['user']['name'].' '.$data['user']['family_name']}},                                        </td>                                    </tr>                                    <tr style="display: block;width: 100%;">                                        <td style="                                            display: block;                                            width: 100%;                                            color: rgba(59,59,59,0.82);                                            font-size: 20px;                                            text-align: left;                                            font-weight: normal;                                            padding-bottom: 28px;                                        ">										{{$data['heading']}}                                        </td>                                    </tr>                                    <tr>                                        <td style="                                            color: rgba(59,59,59,0.82);                                            font-size: 20px;                                            font-weight: normal;                                            padding-bottom: 50px;                                        ">                                            Surefix                                        </td>                                    </tr>                                </tbody>                            </table>                        </td>                    </tr>                </tbody>                <tfoot>                    <tr>                        <th style="                            padding: 12px 35px;                            text-align: center;                            background-color: #45b2e3;                            color: #fff;                            font-size: 17px;                            font-weight: normal;                        ">                            Copyright &copy; {{$carbon::now()->year}}  - All Rights Reserved                        </th>                    </tr>                </tfoot>            </table>        </td>    </tr>    </tbody></table></body></html>